import React, { useState } from "react";
import { Chessboard } from "react-chessboard";
import { Chess } from "chess.js";

const game = new Chess();

function App() {
  const [fen, setFen] = useState(game.fen());

  function onDrop(sourceSquare, targetSquare) {
  const move = game.move({
    from: sourceSquare,
    to: targetSquare,
    promotion: "q",
  });

  if (move === null) return false;

  setFen(game.fen());

  fetch("http://localhost:8080/api/analizar", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ fen: game.fen() }),
  })
    .then((res) => res.json())
    .then((data) => {
      console.log("Mejor jugada:", data.mejorJugada);
      console.log("Evaluación:", data.evaluacion);
    })
    .catch((err) => {
      console.error("Error analizando la jugada:", err);
    });

  return true;
}


  return <Chessboard position={fen} onPieceDrop={onDrop} />;
}

export default App;
