import React, { useState, useEffect } from "react";
import { Chessboard } from "react-chessboard";
import { Chess } from "chess.js";

const game = new Chess();

function App() {
  const [fen, setFen] = useState(game.fen());
  const [boardWidth, setBoardWidth] = useState(0);

  useEffect(() => {
    const updateBoardSize = () => {
      const padding = 32; // espacio para que no quede pegado
      const width = Math.min(window.innerWidth, window.innerHeight) - padding;
      setBoardWidth(width);
    };

    updateBoardSize();
    window.addEventListener("resize", updateBoardSize);
    return () => window.removeEventListener("resize", updateBoardSize);
  }, []);

  const onDrop = (sourceSquare, targetSquare) => {
    const move = game.move({ from: sourceSquare, to: targetSquare, promotion: "q" });
    if (!move) {
      console.warn("Movimiento ilegal");
      return false;
    }

    setFen(game.fen());

    fetch("http://localhost:8080/api/analizar", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ fen: game.fen() }),
    })
      .then((res) => res.json())
      .then((data) => {
        console.log("Mejor jugada:", data.mejorJugada);
        console.log("Evaluación:", data.evaluacion);
      });

    return true;
  };

  return (
    <div
      style={{
        width: "100vw",
        height: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      {boardWidth > 0 && (
        <Chessboard position={fen} onPieceDrop={onDrop} boardWidth={boardWidth} />
      )}
    </div>
  );
}

export default App;
