import React, { useState, useEffect } from "react";
import { Chessboard } from "react-chessboard";
import { Chess } from "chess.js";

const game = new Chess();

function App() {
  const [fen, setFen] = useState(game.fen());
  const [boardWidth, setBoardWidth] = useState(window.innerWidth < 500 ? window.innerWidth - 20 : 500);

  useEffect(() => {
    const handleResize = () => {
      const width = window.innerWidth < 500 ? window.innerWidth - 20 : 500;
      setBoardWidth(width);
    };
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  function onDrop(sourceSquare, targetSquare) {
    try {
      const move = game.move({ from: sourceSquare, to: targetSquare, promotion: "q" });
      if (move === null) {
        console.warn("Movimiento ilegal");
        return false;
      }

      setFen(game.fen());

      fetch("http://localhost:8080/api/analizar", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ fen: game.fen() }),
      })
        .then((res) => res.json())
        .then((data) => {
          console.log("Mejor jugada:", data.mejorJugada);
          console.log("Evaluación:", data.evaluacion);
        });

      return true;
    } catch (error) {
      console.error("Error al mover la pieza:", error);
      return false;
    }
  }

  return (
    <div style={{ display: "flex", justifyContent: "center", padding: "1rem" }}>
      <Chessboard position={fen} onPieceDrop={onDrop} boardWidth={boardWidth} />
    </div>
  );
}

export default App;
